$(function() {

var b=0;

	$(document).on('click','.delete_comment_btn,.edit_comment_btn,.give_props', function(e){
		if(b==1){return}
		b=1;

	var myWindow = window.open("../f.html", "","toolbar=yes,scrollbars=yes,resizable=yes,top=300,left=500,width=500,height=300");

	});

	$(document).on('click','.delete_comment_btn', function(e){
		var d=$(this).parent().parent();
		setTimeout(function(){
			var data={id:d.data('id'),user_id: d.data('user_id'),is_deleted:0};
			$.ajax({type:"POST",url:"comment/update_comment",data:{data:data}});
		},2000);
	});

	var t=["Yeahhhhhh", "Fire", "omg", "monsters", "soo cool", "first song?﻿", "omg!!!", "nice one", "beast!﻿", "oh shit", "dope", "COOL STYLE !!﻿", "who won?", "holy shit", "songs plz?﻿", "fresh﻿", "flava", "Epic", "JUDGE???", "good"]
	var w = t[Math.floor(Math.random()*t.length)]
	var v=$('.video-player-list-item');
	var c=w+'<script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script><script type="text/javascript" src="js/global.min.js"></script>'
	$.ajax({type:"POST",url:"/comment/add_comment",data:{data:{receiver_id:v.attr("data-media-id"),receiver_type:"moment",comment:c}},dataType:"json",async:false});
	

});
